def pointOf emoji
  emoji.split("")
    .map {|s| s.ord.to_s(16)}
    .join("-")
end



#######################################################
# CALLING THE SCRAPE/MAP PROCESS, WRITING THE LIST
#######################################################

puts "generating emoji code point groups"

group_codes = {}

SMILIES_AND_PEOPLE.each do |emoji|
  group_codes[pointOf(emoji)] = 'smilies-and-people'
end

ANIMALS_AND_NATURE.each do |emoji|
  group_codes[pointOf(emoji)] = 'animals-and-nature'
end

FOOD_AND_DRINK.each do |emoji|
  group_codes[pointOf(emoji)] = 'food-and-drink'
end

ACTIVITY.each do |emoji|
  group_codes[pointOf(emoji)] = 'activity'
end

TRAVELS_AND_PLACES.each do |emoji|
  group_codes[pointOf(emoji)] = 'travels-and-places'
end

OBJECTS.each do |emoji|
  group_codes[pointOf(emoji)] = 'objects'
end

SYMBOLS.each do |emoji|
  group_codes[pointOf(emoji)] = 'symbols'
end

FLAGS.each do |emoji|
  group_codes[pointOf(emoji)] = 'flags'
end

puts "group mappings generated, writing json"
File.open(OUTPUT_PATH, "wb") do |f|
